        console.log('move')
    };
    const handleMouseUp = () => {

        console.log('up')


    };

    return (
        <div>
            <Container
                onMouseDown={handleMouseDown}
                x={translateX}
                y={translateY}
                isDragging={isDragging}
            >
                {children}
            </Container>
        </div>
    )
}
const Container = styled.div.attrs({
    style: ({ x, y }) => ({
        transform: `translate(${x}px, ${y}px)`
    }),
})`
    cursor: grab;

    ${({ isDragging }) =>
        isDragging && css`
      opacity: 0.8;
      cursor: grabbing;
    `};
  `;
