import React, { useState } from 'react'
import '../App.css';
import { Nav, ButtonGroup, Button, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as Icon from 'react-feather';
import { DragDropContainer, DropTarget } from 'react-drag-drop-container';
import Drap from './Drap';
import Draggable from './Draggable';
export default function Homepage() {
    const [modalShow1, setModalShow1] = useState(false);
    const [MousePosition, setMousePosition] = useState({ left: 50, top: 100 });
    const [originalX, setOriginalX] = useState(0);
    const [originalY, setOriginalY] = useState(0);
    const [translate, setTranslate] = useState({ left: 0, top: 0 });

    const [lastTranslateX, setlastTranslateX] = useState(0);
    const [lastTranslateY, setlastTranslateY] = useState(0);
    const [state, setState] = useState(false)
    const [state1, setState1] = useState(false)
    const handleOver = () => {
        setState1(true);
        console.log('helo')

    }

    const handleOUT = () => {
        setState1(false);

    }

    const handleMouseDown = (event) => {

        setState(true);

        if (state1 == true) {

            handleMouseMove(translate)
        }



    }

    const handleMouseMove = (event) => {


        if (state == true && state1 == true) {
            setTranslate({
                ...translate,
                left: event.clientX,
                top: event.clientY
            })
            setMousePosition({
                ...MousePosition,
                left: event.clientX,
                top: event.clientY
            })
            console.log(MousePosition)
        }

    };
    const handleMouseUp = (event) => {
        setState(false);
        setMousePosition({
            ...MousePosition,
            left: event.clientX,
            top: event.clientY
        })
        console.log(MousePosition)


    };

    return (
        <div style={{ backgroundColor: '#260026', display: "flex", justifyContent: 'space-between' }} >


            <div className='md-col-1' style={{ height: '741px', }}>

                <ButtonGroup style={{ display: "block" }}>
                    <Button style={{ height: '60px', backgroundColor: '#260026', borderRadius: '0px', border: '0px', color: '#d79f47' }}>
                        <Icon.RefreshCw />
                        <div>
                            <span>Clean</span>
                        </div>
                    </Button>
                    <Button style={{ height: '60px', backgroundColor: '#260026', borderRadius: '0px', border: '0px', color: '#d79f47' }} onClick={() => setModalShow1(true)}>
                        <Icon.Settings />
                        <div>
                            <span>Setting</span>
                        </div>
                    </Button>


                    <Modal

                        show={modalShow1}
                        onHide={() => setModalShow1(false)}

                    >
                        <Modal.Header style={{ backgroundColor: '#fcce84', border: '0px' }}>
                            <Modal.Title id="contained-modal-title-vcenter" >
                                <div className='col-12' style={{ display: 'flex', justifyContent: 'flex-start' }}>
                                    <div style={{ height: '50px', width: '50px', backgroundColor: '#927be3', alignItems: 'center', justifyContent: "center", alignContent: 'center', display: "flex", borderRadius: '5px', marginLeft: '-35px', color: 'white' }}>
                                        <Icon.XCircle></Icon.XCircle>
                                    </div>
                                    <div className='col-10 mx-auto' ><h4 style={{ justifyContent: "center", alignContent: 'center', display: "flex", }}>SETTINGS</h4></div>
                                </div>
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body style={{ backgroundColor: '#f8c46b' }}>
                            <ButtonGroup vertical className='col-12'>
                                {/* <Button className="col-sm-10 mx-auto  mb-2" variant="secondary" size="lg">left</Button> */}
                                <div className='col-sm-10 mx-auto mb-2' style={{ justifyContent: 'space-between', display: "flex" }}>
                                    <Button className='col-sm-3'>left</Button>
                                    <Button className='col-sm-3'>left</Button>
                                </div>
                                <Button className="col-sm-10 mx-auto  mb-2" variant="secondary" size="lg">Middle</Button>
                                <Button className="col-sm-10 mx-auto mb-2" variant="secondary" size="lg">Right</Button>
                                <Button className="col-sm-10 mx-auto mb-2" variant="secondary" size="lg">Right</Button>

                            </ButtonGroup>
                        </Modal.Body>
                        <Modal.Footer style={{ backgroundColor: '#f8c46b', border: '0px' }}>

                        </Modal.Footer>
                    </Modal>

                    <Button style={{ height: '60px', backgroundColor: '#260026', borderRadius: '0px', border: '0px', color: '#d79f47' }}>
                        <Icon.BookOpen />
                        <div>
                            <span>Coures</span>
                        </div>
                    </Button>
                    <Button style={{ height: '60px', backgroundColor: '#260026', borderRadius: '0px', border: '0px', color: '#d79f47' }}>
                        <Icon.Globe />
                        <div>
                            <span>Hnitsd</span>
                        </div>
                    </Button>

                </ButtonGroup>
            </div>
            <div className='col-9' onMouseMove={(event) => handleMouseMove(event)}>
                <div>
                    <div className='halo22' style={{ left: MousePosition.left, top: MousePosition.top }}
                        onMouseDown={(event) => handleMouseDown(event)}

                        onMouseOver="handleOver"
                        onMouseUp={(event) => handleMouseUp()}
                        onMouseOut={(event) => handleOUT()}
                    >halo</div>
                </div>
            </div>
            <div className='col-2' style={{ backgroundColor: 'blue' }}>3</div>
        </div >
    )
}
