import React from 'react';
import PropTypes from 'prop-types';

export default class Drapable extends React.Component {
    drap = (e) => {
        e.dataTransfer.setData('transfer', e.target.id);
    }
    noAllowDrop = (e) => {
        e.stopPropagation();
    }
    render() {
        return (
            <div id={this.props.id} drapable='true' onDragstart={this.drag} onDragOver={this.noAllowDrop} style={this.props.style}>
                {this.props.children}
            </div>
        );
    }
}
Drapable.PropTypes = {
    id: PropTypes.string,
    style: PropTypes.object,
    children: PropTypes.none,
}
