import React, { useState } from 'react'
import styled, { css } from 'styled-components';
import '../App.css';
export default function Drap() {
    const [MousePosition, setMousePosition] = useState({ left: 50, top: 100 });
    const [originalX, setOriginalX] = useState(0);
    const [originalY, setOriginalY] = useState(0);
    const [translate, setTranslate] = useState({ left: 0, top: 0 });

    const [lastTranslateX, setlastTranslateX] = useState(0);
    const [lastTranslateY, setlastTranslateY] = useState(0);
    const [state, setState] = useState(false)
    const [state1, setState1] = useState(false)
    const handleOver = () => {
        setState1(true);

    }

    const handleOUT = () => {
        setState1(false);

    }

    const handleMouseDown = (event) => {

        setState(true);

        if (state1 == true) {

            handleMouseMove(event)
        }



    }

    const handleMouseMove = (event) => {

        // console.log(state)
        // if (state1 == true) {
        //     setTranslate({
        //         ...translate,
        //         left: event.clientX,
        //         top: event.clientY
        //     })
        //     console.log(translate)
        // }
        const x = event.clientX;
        const y = event.clientY;
        console.log(x, y)
    };
    const handleMouseUp = (event) => {
        setState(false);
        // setMousePosition({
        //     ...MousePosition,
        //     left: event.clientX,
        //     top: event.clientY
        // })
        // console.log(MousePosition)


    };



    // function handleMouseMove(ev) { setMousePosition({left: ev.pageX, top: ev.pageY}); }
    return (
        <div onMouseMove={(event) => handleMouseMove(event)}>

            <div className='halo22' style={{ left: MousePosition.left, top: MousePosition.top }}
                onMouseDown={(event) => handleMouseDown(event)}
                // onMouseMove={(event) => handleMouseMove(event)}
                onMouseOver={(event) => handleOver()}
                onMouseUp={(event) => handleMouseUp()}
                onMouseOut={(event) => handleOUT()}
            >halo</div>
        </div>
    )
}
